import { useState } from 'react';
import { toast } from 'react-toastify';

const EventForm = ({ eventData, onSubmit, onCancel }) => {
  const [formData, setFormData] = useState({
    title: eventData?.title || '',
    description: eventData?.description || '',
    date: eventData?.date ? new Date(eventData.date).toISOString().slice(0, 16) : '',
    location: eventData?.location || '',
    maxParticipants: eventData?.maxParticipants || 50
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: name === 'maxParticipants' ? parseInt(value) : value
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!formData.title || !formData.description || !formData.date || !formData.location) {
      toast.error('Please fill all fields');
      return;
    }
    onSubmit(formData);
  };

  return (
    <div className="form-modal">
      <form onSubmit={handleSubmit}>
        <h2>{eventData?._id ? 'Edit Event' : 'Create Event'}</h2>
        
        <label>Event Title</label>
        <input
          type="text"
          name="title"
          value={formData.title}
          onChange={handleChange}
          required
        />

        <label>Description</label>
        <textarea
          name="description"
          value={formData.description}
          onChange={handleChange}
          required
        />

        <label>Date & Time</label>
        <input
          type="datetime-local"
          name="date"
          value={formData.date}
          onChange={handleChange}
          required
        />

        <label>Location</label>
        <input
          type="text"
          name="location"
          value={formData.location}
          onChange={handleChange}
          required
        />

        <label>Max Participants</label>
        <input
          type="number"
          name="maxParticipants"
          value={formData.maxParticipants}
          onChange={handleChange}
          min="1"
          required
        />

        <div className="form-actions">
          <button type="button" onClick={onCancel} className="btn btn-cancel">
            Cancel
          </button>
          <button type="submit" className="btn btn-submit">
            {eventData?._id ? 'Update' : 'Create'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default EventForm;
