import { Link } from 'react-router-dom';

const EventCard = ({ event }) => {
  const isFull = event.registeredParticipants.length >= event.maxParticipants;

  return (
    <div className={`event-card ${isFull ? 'card-disabled' : ''}`}>
      <h3>{event.title}</h3>
      <div className="card-details">
        <p>üìÖ {new Date(event.date).toLocaleString()}</p>
        <p>üìç {event.location}</p>
        <p className="participants">
          üë• {event.registeredParticipants.length}/{event.maxParticipants}
        </p>
      </div>
      <Link 
        to={`/event/${event._id}`} 
        className={`btn ${isFull ? 'btn-disabled' : ''}`}
      >
        {isFull ? 'FULL' : 'View Details'}
      </Link>
    </div>
  );
};

export default EventCard;
